// BitCoinTableWatcher.js
import React, { Component, Img } from 'react';
import App from '../App'
import { Menu, Icon, Layout, Breadcrumb, Table } from 'antd';

class BitcoinTableWatcher extends Component {
  constructor (props) {
    super(Component)
    this.dataSource = props.dataSource;
    this.columns = props.columns;
  }
  state = {
    data : {}
  }
  componentDidMount () {
    fetch("http://api.bitcoincharts.com/v1/weighted_prices.json")
      .then(res => res.json())
      .then(json => this.setState({ data: json }))
  }
  render () {
    return (
      <div>
        <ul>
          <li>{this.state.data}</li>
        </ul>
      </div>
    )
  }
}

export default BitcoinTableWatcher;
