// BitCoinTableWatcher.js
import React, { Component, Img } from 'react';
import App from '../App'
import { Menu, Icon, Layout, Breadcrumb, Table } from 'antd';

class BitcoinTableWatcher extends Component {
  constructor (props) {
    super(Component)
    this.dataSource = props.dataSource;
    this.columns = props.columns;
  }
  state = {
    data : []
  }
  async getBitcoinData () {
    let bitcoin = await fetch("http://api.bitcoincharts.com/v1/weighted_prices.json").then(_data => {
      this.state.data.force(_data)
    })
    return <span>{this.state.data}</span>
  }
  render () {
    return (
      <div>
      {this.getBitcoinData}
      </div>
    )
  }
}

export default BitcoinTableWatcher;
