// eslint-disable-next-line
import React, { Component, Img } from 'react';
// eslint-disable-next-line
import { BrowserRouter as Router, Route, Link, Switch, Redirect } from 'react-router-dom';
// eslint-disable-next-line
import { Card, Badge, Row, Col,
  Button, Menu, Icon, Layout, Avatar} from 'antd';

// partials
import SideNav from './partials/sider';
import MainContainer from './partials/MainContainer';
import SpanContainer from './partials/SpanContainer';
import AccessKey from './partials/AccessKey';
// css
import './App.css';
import './app.min.css';
import 'antd/dist/antd.css';
// eslint-disable-next-line

const {
  Header, Content, Footer, Sider,
} = Layout;

const routing = () => (
  <Router>
    <div>
      <Link to='/AccessKey'>Data</Link>
      <Route path='./partials/AccessKey' component={AccessKey}/>
    </div>
  </Router>
)

class App extends Component {
  constructor (props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.input = props.input;
  }
  state = {
    show: false,
    style: 'display: hidden;'
  }
  handleSubmit (e) {
    let style = {
      display: 'none'
    }
    if (this.input.value === ('test' || '12345')) {
      this.state.show = true;

    }
  }
  render () {
    return (
      <div>
      <form onSubmit={this.handleSubmit}>
        <label> Enter Access Key
          <input type="text" ref={(input) => this.input = input}/>
        </label>
        <input type="submit" value="Submit"/>
      </form>
      <AccessKey style={this.state.style}/>
      </div>
    );
  }
}
/*<Layout>
<SideNav/>
<Layout style={{ marginLeft: 200 }}>
  <SpanContainer show={false}/>
  <Footer style={{ textAlign: 'center' }}>
    Designed by Nicky Laczko
  </Footer>
</Layout>
</Layout>*/
export default App;
